mapper.update:
UPDATE students SET name = #{name}, kana_name = #{kanaName}, nick_name = #{nickName}, email = #{email}, area = #{area}, age = #{age}, sex = #{sex}, remark = #{remark}
, isDeleted = #{isDeleted}
WHERE id = #{id}

UPDATE students
SET isDeleted = true
WHERE id = #{id}

UPDATE students
SET isDeleted = false
WHERE id = #{id}

UPDATE course_application_status
SET isDeleted = true
WHERE id = #{id}
